mapper.update:
UPDATE EVENT
SET ECOUNT = ECOUNT + 1
WHERE ENO=#{eNo}

UPDATE EVENT
SET E_STATUS = 'N'
WHERE ENO=#{eNo}

mapper.select:

SELECT *
FROM EVENT



			AND ETITLE LIKE #{sv}


			AND ECONTENT LIKE #{sv}


			AND (ETITLE LIKE #{sv}
				OR ECONTENT LIKE #{sv})



ORDER BY ENO DESC

mapper.update:
UPDATE MEMBER
SET POINT = POINT + #{point}
WHERE MNO=#{mNo}

mapper.select:
SELECT *
FROM PHISTORY
WHERE ENO=#{eNo}
AND MNO=#{mNo}


SELECT COUNT(*)
FROM EVENT

E_STATUS='Y'


			AND ETITLE LIKE #{sv}


			AND ECONTENT LIKE #{sv}


			AND (ETITLE LIKE #{sv}
				OR ECONTENT LIKE #{sv})
