mapper.update:
UPDATE MEMBER
SET M_STATUS='N'
WHERE MID=#{mId}

mapper.select:
SELECT COUNT(*)
FROM MEMBER
WHERE MID=#{mId}

SELECT *
FROM MEMBER
WHERE MNAME=#{mName}
AND MEMAIL=#{mEmail}

SELECT *
FROM MEMBER
WHERE MNAME=#{mName}
AND MEMAIL=#{mEmail}
AND MID=#{mId}

mapper.update:
UPDATE MEMBER
SET MPWD=#{mPwd},M_MODIFY_DATE=SYSDATE
WHERE MNO=#{mNo}

UPDATE MEMBER
SET M_STATUS = 'N'
WHERE MNO=#{mNo}

UPDATE MEMBER
SET M_STATUS = 'Y'
WHERE MNO=#{mNo}

UPDATE STORE
SET SNAME=#{sName}, STEL=#{sTel}, S_ADDRESS=#{sAddress}, DELIVERY_KM=#{deliveryKm},
						 SINFO=#{sInfo}, OPEN_TIME=#{openTime}, S_CATEGORY=#{sCategory}, DELIVERY_CHARGE=#{deliveryCharge},
						 MIN_PRICE=#{minPrice}, SOFF=#{sOff}
WHERE MNO = #{mNo}

UPDATE MENU
SET MN_STATUS = 'S'
WHERE MN_NO =#{mnNo}

UPDATE MENU
SET MN_NAME=#{mnName}, MN_PRICE=#{mnPrice}
WHERE MN_NO = #{mnNo}

mapper.select:
SELECT SUM(OPRICE)
FROM SORDER
WHERE MNO = #{mNo}

mapper.update:
UPDATE MEMBER
SET POINT = POINT - #{oPoint}, PRICE_SUM = PRICE_SUM + #{oPrice} 
WHERE MNO = #{mNo}
